        { id: 56, seek: 190, start: 190, end: 194, start_time: "00:03:10,000", end_time: "00:03:14,000", text: "没有氧分的时候心肌细胞开始死亡。", original_text: "没有氧分的时候心肌细胞开始死亡。" },
        { id: 57, seek: 194, start: 194, end: 197, start_time: "00:03:14,000", end_time: "00:03:17,000", text: "如果栓塞堵住了大脑的血流，", original_text: "如果栓塞堵住了大脑的血流，" },
        { id: 58, seek: 197, start: 197, end: 200, start_time: "00:03:17,000", end_time: "00:03:20,000", text: "就引起了中风。", original_text: "就引起了中风。" },
        { id: 59, seek: 200, start: 200, end: 202, start_time: "00:03:20,000", end_time: "00:03:22,000", text: "危险的栓塞血管可以", original_text: "危险的栓塞血管可以" },
        { id: 60, seek: 202, start: 202, end: 205, start_time: "00:03:22,000", end_time: "00:03:25,000", text: "被通过血管成形术的手术扩大。", original_text: "被通过血管成形术的手术扩大。" },
        { id: 61, seek: 205, start: 205, end: 208, start_time: "00:03:25,000", end_time: "00:03:28,000", text: "医生在血管中放置一条细管", original_text: "医生在血管中放置一条细管" },
        { id: 62, seek: 208, start: 208, end: 210, start_time: "00:03:28,000", end_time: "00:03:30,000", text: "到栓塞的地方，", original_text: "到栓塞的地方，" },
        { id: 63, seek: 210, start: 210, end: 214, start_time: "00:03:30,000", end_time: "00:03:34,000", text: "然后在细管上放置一个未充气的气球导管", original_text: "然后在细管上放置一个未充气的气球导管" },
        { id: 64, seek: 214, start: 214, end: 218, start_time: "00:03:34,000", end_time: "00:03:38,000", text: "当气球充气的时候，它会强迫同道再次打开。", original_text: "当气球充气的时候，它会强迫同道再次打开。" },
        { id: 65, seek: 218, start: 218, end: 221, start_time: "00:03:38,000", end_time: "00:03:41,000", text: "有时一条硬性管道叫做支架", original_text: "有时一条硬性管道叫做支架" },
        { id: 66, seek: 221, start: 221, end: 225, start_time: "00:03:41,000", end_time: "00:03:45,000", text: "会放入血管使其张开，", original_text: "会放入血管使其张开，" },
        { id: 67, seek: 225, start: 225, end: 226, start_time: "00:03:45,000", end_time: "00:03:46,000", text: "让血液自由地流动", original_text: "让血液自由地流动" },
        { id: 68, seek: 226, start: 226, end: 230, start_time: "00:03:46,000", end_time: "00:03:50,000", text: "来补充下面管道里缺氧的细胞。", original_text: "来补充下面管道里缺氧的细胞。" },
        { id: 69, seek: 230, start: 230, end: 233, start_time: "00:03:50,000", end_time: "00:03:53,000", text: "在压力下保持弹性对动脉是一种艰难的工作。", original_text: "在压力下保持弹性对动脉是一种艰难的工作。" },
        { id: 70, seek: 233, start: 233, end: 236, start_time: "00:03:53,000", end_time: "00:03:56,000", text: "它们泵出的液体是由一些", original_text: "它们泵出的液体是由一些" },
        { id: 71, seek: 236, start: 236, end: 239, start_time: "00:03:56,000", end_time: "00:03:59,000", text: "能够粘连并堵住它们的物质组成，", original_text: "能够粘连并堵住它们的物质组成，" },
        { id: 72, seek: 239, start: 239, end: 242, start_time: "00:03:59,000", end_time: "00:04:02,000", text: "通常你的心跳在70次每分钟，", original_text: "通常你的心跳在70次每分钟，" },
        { id: 73, seek: 242, start: 242, end: 247, start_time: "00:04:02,000", end_time: "00:04:07,000", text: "在平均寿命中至少跳上250亿次", original_text: "在平均寿命中至少跳上250亿次" },
        { id: 74, seek: 247, start: 247, end: 250, start_time: "00:04:07,000", end_time: "00:04:10,000", text: "这听起来像不可逾越的一堆压量，", original_text: "这听起来像不可逾越的一堆压量，" },
        { id: 75, seek: 250, start: 250, end: 262, start_time: "00:04:10,000", end_time: "00:04:22,000", text: "但是别担心，你的动脉为此挑战而装备完好。", original_text: "但是别担心，你的动脉为此挑战而装备完好。" }
