export interface RecommendedVideoSegment {
  id: number
  seek: number
  start: number
  end: number
  start_time: string  // HH:MM:SS,mmm 형식 추가
  end_time: string    // HH:MM:SS,mmm 형식 추가
  text: string
  keywords?: string[]
  original_text?: string
}

export interface RecommendedVideo {
  id: string
  title: string
  channel: string
  duration: string
  views: string
  uploadTime: string
  thumbnail: string
  url: string
  description: string
  verified: boolean
  // 난이도 및 카테고리 분류
  difficulty: 'easy' | 'medium' | 'hard'  // 난이도: 쉬움, 보통, 어려움
  category: 'news' | 'education' | 'entertainment' | 'culture' | 'technology' | 'business' | 'history' | 'comedy' | 'documentary'  // 카테고리
  tags: string[]  // 태그 배열
  // 사전 변환된 데이터
  processedData?: {
    text: string
    segments: RecommendedVideoSegment[]
    language: string
    processed_at: string
  }
}

// 하드코딩된 추천 영상 데이터에는 이미 start_time과 end_time 필드가 포함되어 있습니다

// 난이도별 정렬 함수
export function sortVideosByDifficulty(videos: RecommendedVideo[], order: 'asc' | 'desc' = 'desc'): RecommendedVideo[] {
  const difficultyOrder = { 'hard': 3, 'medium': 2, 'easy': 1 };
  
  return [...videos].sort((a, b) => {
    const aOrder = difficultyOrder[a.difficulty];
    const bOrder = difficultyOrder[b.difficulty];
    
    if (order === 'desc') {
      return bOrder - aOrder; // 어려움 -> 보통 -> 쉬움
    } else {
      return aOrder - bOrder; // 쉬움 -> 보통 -> 어려움
    }
  });
}

