        { id: 48, seek: 134, start: 134, end: 137, start_time: "00:02:14,000", end_time: "00:02:17,000", text: "这位大使回到中国之后，向当时的汉朝皇帝", original_text: "这位大使回到中国之后，向当时的汉朝皇帝" },
        { id: 49, seek: 137, start: 137, end: 140, start_time: "00:02:17,000", end_time: "00:02:20,000", text: "讲述了许多有关其边界西部地区古老的文明，", original_text: "讲述了许多有关其边界西部地区古老的文明，" },
        { id: 50, seek: 140, start: 140, end: 143, start_time: "00:02:20,000", end_time: "00:02:23,000", text: "蓬勃的贸易，以及奇异的货物的故事。", original_text: "蓬勃的贸易，以及奇异的货物的故事。" },
        { id: 51, seek: 143, start: 143, end: 146, start_time: "00:02:23,000", end_time: "00:02:26,000", text: "接着许多大使、商人相继被派往波斯和印度，", original_text: "接着许多大使、商人相继被派往波斯和印度，" },
        { id: 52, seek: 146, start: 146, end: 150, start_time: "00:02:26,000", end_time: "00:02:30,000", text: "用丝绸和玉交换他们的马和棉花。", original_text: "用丝绸和玉交换他们的马和棉花。" },
        { id: 53, seek: 150, start: 150, end: 153, start_time: "00:02:30,000", end_time: "00:02:33,000", text: "当时随从的还有军队，以保证他们旅途的安全。", original_text: "当时随从的还有军队，以保证他们旅途的安全。" },
        { id: 54, seek: 153, start: 153, end: 155, start_time: "00:02:33,000", end_time: "00:02:35,000", text: "东方和西方之间的道路逐渐形成了", original_text: "东方和西方之间的道路逐渐形成了" },
        { id: 55, seek: 155, start: 155, end: 158, start_time: "00:02:35,000", end_time: "00:02:38,000", text: "一个跨欧亚的网络，", original_text: "一个跨欧亚的网络，" },
        { id: 56, seek: 158, start: 158, end: 160, start_time: "00:02:38,000", end_time: "00:02:40,000", text: "这一网络使得欧亚之间的文化和商业交往程度", original_text: "这一网络使得欧亚之间的文化和商业交往程度" },
        { id: 57, seek: 160, start: 160, end: 162, start_time: "00:02:40,000", end_time: "00:02:42,000", text: "达到了当时的历史之最。", original_text: "达到了当时的历史之最。" },
        { id: 58, seek: 162, start: 162, end: 164, start_time: "00:02:42,000", end_time: "00:02:44,000", text: "中国的货物来到了罗马，", original_text: "中国的货物来到了罗马，" },
        { id: 59, seek: 164, start: 164, end: 168, start_time: "00:02:44,000", end_time: "00:02:48,000", text: "造成大量的黄金外流，因此罗马禁止了丝绸交易。", original_text: "造成大量的黄金外流，因此罗马禁止了丝绸交易。" },
        { id: 60, seek: 168, start: 168, end: 171, start_time: "00:02:48,000", end_time: "00:02:51,000", text: "罗马的玻璃器具在中国也十分受欢迎，", original_text: "罗马的玻璃器具在中国也十分受欢迎，" },
        { id: 61, seek: 171, start: 171, end: 173, start_time: "00:02:51,000", end_time: "00:02:53,000", text: "中亚的远征军", original_text: "中亚的远征军" },
        { id: 62, seek: 173, start: 173, end: 177, start_time: "00:02:53,000", end_time: "00:02:57,000", text: "看到了中国和罗马军队的相遇。", original_text: "看到了中国和罗马军队的相遇。" },
        { id: 63, seek: 177, start: 177, end: 179, start_time: "00:02:57,000", end_time: "00:02:59,000", text: "石弓技术", original_text: "石弓技术" },
        { id: 64, seek: 179, start: 179, end: 181, start_time: "00:02:59,000", end_time: "00:03:01,000", text: "传到西方。", original_text: "传到西方。" },
        { id: 65, seek: 181, start: 181, end: 184, start_time: "00:03:01,000", end_time: "00:03:04,000", text: "对于异国货物的需求，", original_text: "对于异国货物的需求，" },
        { id: 66, seek: 184, start: 184, end: 185, start_time: "00:03:04,000", end_time: "00:03:05,000", text: "贸易带来的利益，", original_text: "贸易带来的利益，" },
        { id: 67, seek: 185, start: 185, end: 187, start_time: "00:03:05,000", end_time: "00:03:07,000", text: "使得这条丝绸之路一直保存完好，", original_text: "使得这条丝绸之路一直保存完好，" },
        { id: 68, seek: 187, start: 187, end: 189, start_time: "00:03:07,000", end_time: "00:03:09,000", text: "尽管罗马帝国分崩离析，", original_text: "尽管罗马帝国分崩离析，" },
        { id: 69, seek: 189, start: 189, end: 193, start_time: "00:03:09,000", end_time: "00:03:13,000", text: "中国的朝代盛衰兴废。", original_text: "中国的朝代盛衰兴废。" },
        { id: 70, seek: 193, start: 193, end: 196, start_time: "00:03:13,000", end_time: "00:03:16,000", text: "即使是那些以抢劫和掠夺臭名昭著的蒙古囤积商人，", original_text: "即使是那些以抢劫和掠夺臭名昭著的蒙古囤积商人，" },
        { id: 71, seek: 196, start: 196, end: 199, start_time: "00:03:16,000", end_time: "00:03:19,000", text: "也积极地保护这些贸易通道，而不是破坏它们。", original_text: "也积极地保护这些贸易通道，而不是破坏它们。" },
        { id: 72, seek: 199, start: 199, end: 202, start_time: "00:03:19,000", end_time: "00:03:22,000", text: "随着商品经济的发达，这条道路", original_text: "随着商品经济的发达，这条道路" },
        { id: 73, seek: 202, start: 202, end: 207, start_time: "00:03:22,000", end_time: "00:03:27,000", text: "同时也使得文化，创新技术，思想和语言得以流通。", original_text: "同时也使得文化，创新技术，思想和语言得以流通。" },
        { id: 74, seek: 207, start: 207, end: 210, start_time: "00:03:27,000", end_time: "00:03:30,000", text: "发源于印度，佛教传播到了中国和日本", original_text: "发源于印度，佛教传播到了中国和日本" },
        { id: 75, seek: 210, start: 210, end: 212, start_time: "00:03:30,000", end_time: "00:03:32,000", text: "成为亚洲地区最盛行的宗教。", original_text: "成为亚洲地区最盛行的宗教。" },
        { id: 76, seek: 212, start: 212, end: 216, start_time: "00:03:32,000", end_time: "00:03:36,000", text: "伊斯兰教从阿拉伯半岛传到南亚，", original_text: "伊斯兰教从阿拉伯半岛传到南亚，" },
        { id: 77, seek: 216, start: 216, end: 220, start_time: "00:03:36,000", end_time: "00:03:40,000", text: "和当地的信仰融合", original_text: "和当地的信仰融合" },
        { id: 78, seek: 220, start: 220, end: 223, start_time: "00:03:40,000", end_time: "00:03:43,000", text: "形成新的宗教，比如锡克教。", original_text: "形成新的宗教，比如锡克教。" },
        { id: 79, seek: 223, start: 223, end: 227, start_time: "00:03:43,000", end_time: "00:03:47,000", text: "火药从中国传到中东", original_text: "火药从中国传到中东" },
        { id: 80, seek: 227, start: 227, end: 233, start_time: "00:03:47,000", end_time: "00:03:53,000", text: "形塑了土耳其帝国，萨法维帝国，与马拉塔帝国的未来。", original_text: "形塑了土耳其帝国，萨法维帝国，与马拉塔帝国的未来。" },
        { id: 81, seek: 233, start: 233, end: 238, start_time: "00:03:53,000", end_time: "00:03:58,000", text: "某种程度上说，丝绸之路成功地终结了自己。", original_text: "某种程度上说，丝绸之路成功地终结了自己。" },
        { id: 82, seek: 238, start: 238, end: 241, start_time: "00:03:58,000", end_time: "00:04:01,000", text: "新的航海技术，像是磁罗盘，", original_text: "新的航海技术，像是磁罗盘，" },
        { id: 83, seek: 241, start: 241, end: 244, start_time: "00:04:01,000", end_time: "00:04:04,000", text: "传往欧洲，使得陆地上的长程公路被淘汰了。", original_text: "传往欧洲，使得陆地上的长程公路被淘汰了。" },
        { id: 84, seek: 244, start: 244, end: 247, start_time: "00:04:04,000", end_time: "00:04:07,000", text: "与此同时，元朝瓦解后，", original_text: "与此同时，元朝瓦解后，" },
        { id: 85, seek: 247, start: 247, end: 250, start_time: "00:04:07,000", end_time: "00:04:10,000", text: "中国从国际贸易版图消失。", original_text: "中国从国际贸易版图消失。" },
        { id: 86, seek: 250, start: 250, end: 254, start_time: "00:04:10,000", end_time: "00:04:14,000", text: "即便旧的道路和贸易网络没能长久，", original_text: "即便旧的道路和贸易网络没能长久，" },
        { id: 87, seek: 254, start: 254, end: 258, start_time: "00:04:14,000", end_time: "00:04:18,000", text: "它们也永久改变了世界，", original_text: "它们也永久改变了世界，" },
        { id: 88, seek: 258, start: 258, end: 260, start_time: "00:04:18,000", end_time: "00:04:20,000", text: "没有倒退的可能了。", original_text: "没有倒退的可能了。" },
        { id: 89, seek: 260, start: 260, end: 263, start_time: "00:04:20,000", end_time: "00:04:23,000", text: "欧洲人寻找新的海上航道", original_text: "欧洲人寻找新的海上航道" },
        { id: 90, seek: 263, start: 263, end: 265, start_time: "00:04:23,000", end_time: "00:04:25,000", text: "想要寻找东亚的财富", original_text: "想要寻找东亚的财富" },
        { id: 91, seek: 265, start: 265, end: 267, start_time: "00:04:25,000", end_time: "00:04:27,000", text: "导致了其在非洲和美洲的", original_text: "导致了其在非洲和美洲的" },
        { id: 92, seek: 267, start: 267, end: 270, start_time: "00:04:27,000", end_time: "00:04:30,000", text: "探索和扩张。", original_text: "探索和扩张。" },
        { id: 93, seek: 270, start: 270, end: 274, start_time: "00:04:30,000", end_time: "00:04:34,000", text: "今天，全球各国相互联络无时无刻不在影响着我们的生活。", original_text: "今天，全球各国相互联络无时无刻不在影响着我们的生活。" },
        { id: 94, seek: 274, start: 274, end: 277, start_time: "00:04:34,000", end_time: "00:04:37,000", text: "加拿大消费者购买在孟加拉制作的T恤，", original_text: "加拿大消费者购买在孟加拉制作的T恤，" },
        { id: 95, seek: 277, start: 277, end: 280, start_time: "00:04:37,000", end_time: "00:04:40,000", text: "日本观众观看英国电视节目，", original_text: "日本观众观看英国电视节目，" },
        { id: 96, seek: 280, start: 280, end: 284, start_time: "00:04:40,000", end_time: "00:04:44,000", text: "突尼斯人使用美国的软件来组织革命。", original_text: "突尼斯人使用美国的软件来组织革命。" },
        { id: 97, seek: 284, start: 284, end: 288, start_time: "00:04:44,000", end_time: "00:04:48,000", text: "全球化对文化和经济的影响是无可争议的。", original_text: "全球化对文化和经济的影响是无可争议的。" },
        { id: 98, seek: 288, start: 288, end: 290, start_time: "00:04:48,000", end_time: "00:04:50,000", text: "但随之而来的好处与缺点，", original_text: "但随之而来的好处与缺点，" },
        { id: 99, seek: 290, start: 290, end: 293, start_time: "00:04:50,000", end_time: "00:04:53,000", text: "也不是新鲜事了。", original_text: "也不是新鲜事了。" },
        { id: 100, seek: 293, start: 293, end: 295, start_time: "00:04:53,000", end_time: "00:04:55,000", text: "尽管山川，沙漠，海洋", original_text: "尽管山川，沙漠，海洋" },
        { id: 101, seek: 295, start: 295, end: 297, start_time: "00:04:55,000", end_time: "00:04:57,000", text: "那些曾经隔断我们", original_text: "那些曾经隔断我们" },
        { id: 102, seek: 297, start: 297, end: 299, start_time: "00:04:57,000", end_time: "00:04:59,000", text: "被超音速机械，", original_text: "被超音速机械，" },
        { id: 103, seek: 299, start: 299, end: 301, start_time: "00:04:59,000", end_time: "00:05:01,000", text: "海底电缆", original_text: "海底电缆" },
        { id: 104, seek: 301, start: 301, end: 303, start_time: "00:05:01,000", end_time: "00:05:03,000", text: "和穿越空间的信号传输包围，", original_text: "和穿越空间的信号传输包围，" },
        { id: 105, seek: 303, start: 303, end: 306, start_time: "00:05:03,000", end_time: "00:05:06,000", text: "不再是商队跋涉数月了，", original_text: "不再是商队跋涉数月了，" },
        { id: 106, seek: 306, start: 306, end: 308, start_time: "00:05:06,000", end_time: "00:05:08,000", text: "上述一切都不会发生", original_text: "上述一切都不会发生" },
        { id: 107, seek: 308, start: 308, end: 310, start_time: "00:05:08,000", end_time: "00:05:10,000", text: "如果没有先进的文化", original_text: "如果没有先进的文化" },
        { id: 108, seek: 310, start: 310, end: 301, start_time: "00:05:10,000", end_time: "00:05:01,000", text: "这样的先进文化创造了丝绸之路：", original_text: "这样的先进文化创造了丝绸之路：" },
        { id: 109, seek: 301, start: 301, end: 303, start_time: "00:05:01,000", end_time: "00:05:03,000", text: "世界上第一个连接世界的互联网。", original_text: "世界上第一个连接世界的互联网。" }
