        { id: 30, seek: 106, start: 106, end: 111, start_time: "00:01:46,000", end_time: "00:01:51,000", text: "额外的液体增加了血容量和血压，", original_text: "额外的液体增加了血容量和血压，" },
        { id: 31, seek: 111, start: 111, end: 114, start_time: "00:01:51,000", end_time: "00:01:54,000", text: "还有压力， 比如搏斗或搏斗似的反应，", original_text: "还有压力， 比如搏斗或搏斗似的反应，" },
        { id: 32, seek: 114, start: 114, end: 118, start_time: "00:01:54,000", end_time: "00:01:58,000", text: "会释放荷尔蒙，如肾上腺素 或去甲肾上腺素", original_text: "会释放荷尔蒙，如肾上腺素 或去甲肾上腺素" },
        { id: 33, seek: 118, start: 118, end: 120, start_time: "00:01:58,000", end_time: "00:02:00,000", text: "它们能收缩主要的血管，", original_text: "它们能收缩主要的血管，" },
        { id: 34, seek: 120, start: 120, end: 125, start_time: "00:02:00,000", end_time: "00:02:05,000", text: "增加流动的阻力并且升高血压。", original_text: "增加流动的阻力并且升高血压。" },
        { id: 35, seek: 125, start: 125, end: 128, start_time: "00:02:05,000", end_time: "00:02:08,000", text: "血管通常能够很容易地处理这样的波动。", original_text: "血管通常能够很容易地处理这样的波动。" },
        { id: 36, seek: 128, start: 128, end: 133, start_time: "00:02:08,000", end_time: "00:02:13,000", text: "血管壁里的弹性纤维使得血管富有弹性，", original_text: "血管壁里的弹性纤维使得血管富有弹性，" },
        { id: 37, seek: 133, start: 133, end: 138, start_time: "00:02:13,000", end_time: "00:02:18,000", text: "但是你的血压增高多余140/90，", original_text: "但是你的血压增高多余140/90，" },
        { id: 38, seek: 138, start: 138, end: 141, start_time: "00:02:18,000", end_time: "00:02:21,000", text: "我们就把它叫做高血压，如果一直持续，", original_text: "我们就把它叫做高血压，如果一直持续，" },
        { id: 39, seek: 141, start: 141, end: 143, start_time: "00:02:21,000", end_time: "00:02:23,000", text: "可引起严重的问题。", original_text: "可引起严重的问题。" },
        { id: 40, seek: 143, start: 143, end: 146, start_time: "00:02:23,000", end_time: "00:02:26,000", text: "那是因为额外的血管壁 的压力", original_text: "那是因为额外的血管壁 的压力" },
        { id: 41, seek: 146, start: 146, end: 148, start_time: "00:02:26,000", end_time: "00:02:28,000", text: "能引起很小的泪泡，", original_text: "能引起很小的泪泡，" },
        { id: 42, seek: 148, start: 148, end: 150, start_time: "00:02:28,000", end_time: "00:02:30,000", text: "当受伤的组织肿胀起来，", original_text: "当受伤的组织肿胀起来，" },
        { id: 43, seek: 150, start: 150, end: 153, start_time: "00:02:30,000", end_time: "00:02:33,000", text: "炎症反应的物质，", original_text: "炎症反应的物质，" },
        { id: 44, seek: 153, start: 153, end: 156, start_time: "00:02:33,000", end_time: "00:02:36,000", text: "如白细胞会聚集在泪泡周围。", original_text: "如白细胞会聚集在泪泡周围。" },
        { id: 45, seek: 156, start: 156, end: 161, start_time: "00:02:36,000", end_time: "00:02:41,000", text: "脂肪和胆固醇在血液中漂浮，停留，", original_text: "脂肪和胆固醇在血液中漂浮，停留，" },
        { id: 46, seek: 161, start: 161, end: 163, start_time: "00:02:41,000", end_time: "00:02:43,000", text: "最终会形成粥样块状物", original_text: "最终会形成粥样块状物" },
        { id: 47, seek: 163, start: 163, end: 167, start_time: "00:02:43,000", end_time: "00:02:47,000", text: "使得动脉血管内壁僵硬和变厚。", original_text: "使得动脉血管内壁僵硬和变厚。" },
        { id: 48, seek: 167, start: 167, end: 170, start_time: "00:02:47,000", end_time: "00:02:50,000", text: "这种情况就叫做动脉粥样硬化，", original_text: "这种情况就叫做动脉粥样硬化，" },
        { id: 49, seek: 170, start: 170, end: 172, start_time: "00:02:50,000", end_time: "00:02:52,000", text: "这能产生危险的后果。", original_text: "这能产生危险的后果。" },
        { id: 50, seek: 172, start: 172, end: 176, start_time: "00:02:52,000", end_time: "00:02:56,000", text: "如果粥样块状物破裂， 血块在已成形的块状物顶部成型", original_text: "如果粥样块状物破裂， 血块在已成形的块状物顶部成型" },
        { id: 51, seek: 176, start: 176, end: 179, start_time: "00:02:56,000", end_time: "00:02:59,000", text: "堵住已经狭窄的管腔。", original_text: "堵住已经狭窄的管腔。" },
        { id: 52, seek: 179, start: 179, end: 180, start_time: "00:02:59,000", end_time: "00:03:00,000", text: "如果血块够大，", original_text: "如果血块够大，" },
        { id: 53, seek: 180, start: 180, end: 186, start_time: "00:03:00,000", end_time: "00:03:06,000", text: "它能够堵住氧分和营养 传送到下面的细胞", original_text: "它能够堵住氧分和营养 传送到下面的细胞" },
        { id: 54, seek: 186, start: 186, end: 188, start_time: "00:03:06,000", end_time: "00:03:08,000", text: "当这发生在心脏血管中，", original_text: "当这发生在心脏血管中，" },
        { id: 55, seek: 188, start: 188, end: 190, start_time: "00:03:08,000", end_time: "00:03:10,000", text: "就会引起心肌梗塞，", original_text: "就会引起心肌梗塞，" },
        { id: 56, seek: 190, start: 190, end: 194, start_time: "00:03:10,000", end_time: "00:03:14,000", text: "没有氧分的时候心肌细胞开始死亡。", original_text: "没有氧分的时候心肌细胞开始死亡。" },
        { id: 57, seek: 194, start: 194, end: 197, start_time: "00:03:14,000", end_time: "00:03:17,000", text: "如果栓塞堵住了大脑的血流，", original_text: "如果栓塞堵住了大脑的血流，" },
        { id: 58, seek: 197, start: 197, end: 200, start_time: "00:03:17,000", end_time: "00:03:20,000", text: "就引起了中风。", original_text: "就引起了中风。" },
        { id: 59, seek: 200, start: 200, end: 202, start_time: "00:03:20,000", end_time: "00:03:22,000", text: "危险的栓塞血管可以", original_text: "危险的栓塞血管可以" },
        { id: 60, seek: 202, start: 202, end: 205, start_time: "00:03:22,000", end_time: "00:03:25,000", text: "被通过血管成形术的手术扩大。", original_text: "被通过血管成形术的手术扩大。" },
        { id: 61, seek: 205, start: 205, end: 208, start_time: "00:03:25,000", end_time: "00:03:28,000", text: "医生在血管中放置一条细管", original_text: "医生在血管中放置一条细管" },
        { id: 62, seek: 208, start: 208, end: 210, start_time: "00:03:28,000", end_time: "00:03:30,000", text: "到栓塞的地方，", original_text: "到栓塞的地方，" },
        { id: 63, seek: 210, start: 210, end: 214, start_time: "00:03:30,000", end_time: "00:03:34,000", text: "然后在细管上放置一个未充气的气球导管", original_text: "然后在细管上放置一个未充气的气球导管" },
        { id: 64, seek: 214, start: 214, end: 218, start_time: "00:03:34,000", end_time: "00:03:38,000", text: "当气球充气的时候，它会强迫同道再次打开。", original_text: "当气球充气的时候，它会强迫同道再次打开。" },
        { id: 65, seek: 218, start: 218, end: 221, start_time: "00:03:38,000", end_time: "00:03:41,000", text: "有时一条硬性管道叫做支架", original_text: "有时一条硬性管道叫做支架" },
        { id: 66, seek: 221, start: 221, end: 225, start_time: "00:03:41,000", end_time: "00:03:45,000", text: "会放入血管使其张开，", original_text: "会放入血管使其张开，" },
        { id: 67, seek: 225, start: 225, end: 226, start_time: "00:03:45,000", end_time: "00:03:46,000", text: "让血液自由地流动", original_text: "让血液自由地流动" },
        { id: 68, seek: 226, start: 226, end: 230, start_time: "00:03:46,000", end_time: "00:03:50,000", text: "来补充下面管道里缺氧的细胞。", original_text: "来补充下面管道里缺氧的细胞。" },
        { id: 69, seek: 230, start: 230, end: 233, start_time: "00:03:50,000", end_time: "00:03:53,000", text: "在压力下保持弹性对动脉是一种艰难的工作。", original_text: "在压力下保持弹性对动脉是一种艰难的工作。" },
        { id: 70, seek: 233, start: 233, end: 236, start_time: "00:03:53,000", end_time: "00:03:56,000", text: "它们泵出的液体是由一些", original_text: "它们泵出的液体是由一些" },
        { id: 71, seek: 236, start: 236, end: 239, start_time: "00:03:56,000", end_time: "00:03:59,000", text: "能够粘连并堵住它们的物质组成，", original_text: "能够粘连并堵住它们的物质组成，" },
        { id: 72, seek: 239, start: 239, end: 242, start_time: "00:03:59,000", end_time: "00:04:02,000", text: "通常你的心跳在70次每分钟，", original_text: "通常你的心跳在70次每分钟，" },
        { id: 73, seek: 242, start: 242, end: 247, start_time: "00:04:02,000", end_time: "00:04:07,000", text: "在平均寿命中至少跳上250亿次", original_text: "在平均寿命中至少跳上250亿次" },
        { id: 74, seek: 247, start: 247, end: 250, start_time: "00:04:07,000", end_time: "00:04:10,000", text: "这听起来像不可逾越的一堆压量，", original_text: "这听起来像不可逾越的一堆压量，" },
        { id: 75, seek: 250, start: 250, end: 262, start_time: "00:04:10,000", end_time: "00:04:22,000", text: "但是别担心，你的动脉为此挑战而装备完好。", original_text: "但是别担心，你的动脉为此挑战而装备完好。" }
