        { id: 13, seek: 58, start: 58, end: 62, start_time: "00:00:58,000", end_time: "00:01:02,000", text: "古老的美索不达米亚定居点 在房子最私密的位置", original_text: "古老的美索不达米亚定居点 在房子最私密的位置" },
        { id: 14, seek: 62, start: 62, end: 67, start_time: "00:01:02,000", end_time: "00:01:07,000", text: "通常有粘土结构，用于蹲坐。", original_text: "通常有粘土结构，用于蹲坐。" },
        { id: 15, seek: 67, start: 67, end: 71, start_time: "00:01:07,000", end_time: "00:01:11,000", text: "它们与管道相连， 管道中的流动水将排泄污物排入", original_text: "它们与管道相连， 管道中的流动水将排泄污物排入" },
        { id: 16, seek: 71, start: 71, end: 74, start_time: "00:01:11,000", end_time: "00:01:14,000", text: "街道的水渠和污水坑中。", original_text: "街道的水渠和污水坑中。" },
        { id: 17, seek: 74, start: 74, end: 77, start_time: "00:01:14,000", end_time: "00:01:17,000", text: "例如上述在青铜时代蓬勃发展的例子，", original_text: "例如上述在青铜时代蓬勃发展的例子，" },
        { id: 18, seek: 77, start: 77, end: 80, start_time: "00:01:17,000", end_time: "00:01:20,000", text: "以及在印度河流域的某些地区，", original_text: "以及在印度河流域的某些地区，" },
        { id: 19, seek: 80, start: 80, end: 85, start_time: "00:01:20,000", end_time: "00:01:25,000", text: "近乎每个房子的厕所都会与 城市污水系统相连。", original_text: "近乎每个房子的厕所都会与 城市污水系统相连。" },
        { id: 20, seek: 85, start: 85, end: 91, start_time: "00:01:25,000", end_time: "00:01:31,000", text: "古代克里特宫殿的厕所 甚至可以手动冲洗。", original_text: "古代克里特宫殿的厕所 甚至可以手动冲洗。" },
        { id: 21, seek: 91, start: 91, end: 96, start_time: "00:01:31,000", end_time: "00:01:36,000", text: "研究人员并不确定 这些早期的污水设施是怎么被发明的，", original_text: "研究人员并不确定 这些早期的污水设施是怎么被发明的，" },
        { id: 22, seek: 96, start: 96, end: 100, start_time: "00:01:36,000", end_time: "00:01:40,000", text: "但能够确定的是 污水管理是公共卫生的关键。", original_text: "但能够确定的是 污水管理是公共卫生的关键。" },
        { id: 23, seek: 100, start: 100, end: 105, start_time: "00:01:40,000", end_time: "00:01:45,000", text: "未经处理的污水能滋养有害微生物，", original_text: "未经处理的污水能滋养有害微生物，" },
        { id: 24, seek: 105, start: 105, end: 109, start_time: "00:01:45,000", end_time: "00:01:49,000", text: "从而导致霍乱，痢疾以及伤寒等疾病。", original_text: "从而导致霍乱，痢疾以及伤寒等疾病。" },
        { id: 25, seek: 109, start: 109, end: 113, start_time: "00:01:49,000", end_time: "00:01:53,000", text: "然而污水与疾病的关系 至少还需要几千年", original_text: "然而污水与疾病的关系 至少还需要几千年" },
        { id: 26, seek: 113, start: 113, end: 116, start_time: "00:01:53,000", end_time: "00:01:56,000", text: "科学家们才能透彻了解。", original_text: "科学家们才能透彻了解。" },
        { id: 27, seek: 116, start: 116, end: 121, start_time: "00:01:56,000", end_time: "00:02:01,000", text: "不过恶臭的污水气味 与疾病的关系", original_text: "不过恶臭的污水气味 与疾病的关系" },
        { id: 28, seek: 121, start: 121, end: 125, start_time: "00:02:01,000", end_time: "00:02:05,000", text: "早在公元前 100 年就有所记载。", original_text: "早在公元前 100 年就有所记载。" },
        { id: 29, seek: 125, start: 125, end: 130, start_time: "00:02:05,000", end_time: "00:02:10,000", text: "到了公元 100 年， 更复杂的卫生解决方案问世。", original_text: "到了公元 100 年， 更复杂的卫生解决方案问世。" },
        { id: 30, seek: 130, start: 130, end: 134, start_time: "00:02:10,000", end_time: "00:02:14,000", text: "罗马帝国采用持续流动的水道，", original_text: "罗马帝国采用持续流动的水道，" },
        { id: 31, seek: 134, start: 134, end: 138, start_time: "00:02:14,000", end_time: "00:02:18,000", text: "将废水引至城市外围。", original_text: "将废水引至城市外围。" },
        { id: 32, seek: 138, start: 138, end: 143, start_time: "00:02:18,000", end_time: "00:02:23,000", text: "同时期的中国也有私人和公共厕所，", original_text: "同时期的中国也有私人和公共厕所，" },
        { id: 33, seek: 143, start: 143, end: 146, start_time: "00:02:23,000", end_time: "00:02:26,000", text: "只是在中国， 这些排泄污物是被即时循环使用的。", original_text: "只是在中国， 这些排泄污物是被即时循环使用的。" },
        { id: 34, seek: 146, start: 146, end: 149, start_time: "00:02:26,000", end_time: "00:02:29,000", text: "家用厕所的粪便大多会用于喂猪，", original_text: "家用厕所的粪便大多会用于喂猪，" },
        { id: 35, seek: 149, start: 149, end: 154, start_time: "00:02:29,000", end_time: "00:02:34,000", text: "而公共厕所的代谢物会由 专门的人来收集", original_text: "而公共厕所的代谢物会由 专门的人来收集" },
        { id: 36, seek: 154, start: 154, end: 156, start_time: "00:02:34,000", end_time: "00:02:36,000", text: "并制成肥料卖出。", original_text: "并制成肥料卖出。" },
        { id: 37, seek: 156, start: 156, end: 160, start_time: "00:02:36,000", end_time: "00:02:40,000", text: "中国的传统粪便污废物处理方法 持续了好几个世纪，", original_text: "中国的传统粪便污废物处理方法 持续了好几个世纪，" },
        { id: 38, seek: 160, start: 160, end: 163, start_time: "00:02:40,000", end_time: "00:02:43,000", text: "但是在欧洲，罗马帝国的衰落", original_text: "但是在欧洲，罗马帝国的衰落" },
        { id: 39, seek: 163, start: 163, end: 167, start_time: "00:02:43,000", end_time: "00:02:47,000", text: "把公共卫生带入了黑暗时期。", original_text: "把公共卫生带入了黑暗时期。" },
        { id: 40, seek: 167, start: 167, end: 171, start_time: "00:02:47,000", end_time: "00:02:51,000", text: "坑式蹲厕（也被称作“Gong”） 司空见惯，", original_text: "坑式蹲厕（也被称作“Gong”） 司空见惯，" },
        { id: 41, seek: 171, start: 171, end: 174, start_time: "00:02:51,000", end_time: "00:02:54,000", text: "夜壶常常直接往街道上倾倒。", original_text: "夜壶常常直接往街道上倾倒。" },
        { id: 42, seek: 174, start: 174, end: 179, start_time: "00:02:54,000", end_time: "00:02:59,000", text: "城堡的高窗向外面的公共粪池 抛弃排泄污物。", original_text: "城堡的高窗向外面的公共粪池 抛弃排泄污物。" },
        { id: 43, seek: 179, start: 179, end: 183, start_time: "00:02:59,000", end_time: "00:03:03,000", text: "到了晚上，掏粪工 会把排泄污物装满推车，", original_text: "到了晚上，掏粪工 会把排泄污物装满推车，" },
        { id: 44, seek: 183, start: 183, end: 187, start_time: "00:03:03,000", end_time: "00:03:07,000", text: "将它们运出城外并丢弃。", original_text: "将它们运出城外并丢弃。" },
        { id: 45, seek: 187, start: 187, end: 191, start_time: "00:03:07,000", end_time: "00:03:11,000", text: "几个世纪以来， 欧洲都没有对排泄污物进行卫生处理，", original_text: "几个世纪以来， 欧洲都没有对排泄污物进行卫生处理，" },
        { id: 46, seek: 191, start: 191, end: 194, start_time: "00:03:11,000", end_time: "00:03:14,000", text: "但是厕所本身 却经历了一些重大变革。", original_text: "但是厕所本身 却经历了一些重大变革。" },
        { id: 47, seek: 194, start: 194, end: 199, start_time: "00:03:14,000", end_time: "00:03:19,000", text: "到中世纪后期， 大多数富裕人家都配备了坐便器——", original_text: "到中世纪后期， 大多数富裕人家都配备了坐便器——" },
        { id: 48, seek: 199, start: 199, end: 202, start_time: "00:03:19,000", end_time: "00:03:22,000", text: "带有盖子和座椅的木箱子。", original_text: "带有盖子和座椅的木箱子。" },
        { id: 49, seek: 202, start: 202, end: 204, start_time: "00:03:22,000", end_time: "00:03:24,000", text: "在英格兰的皇室宫廷中，", original_text: "在英格兰的皇室宫廷中，" },
        { id: 50, seek: 204, start: 204, end: 208, start_time: "00:03:24,000", end_time: "00:03:28,000", text: "这些座便器由专门的 管理排泄污物的侍从负责。", original_text: "这些座便器由专门的 管理排泄污物的侍从负责。" },
        { id: 51, seek: 208, start: 208, end: 212, start_time: "00:03:28,000", end_time: "00:03:32,000", text: "另外，为了监测国王的肠道健康，", original_text: "另外，为了监测国王的肠道健康，" },
        { id: 52, seek: 212, start: 212, end: 215, start_time: "00:03:32,000", end_time: "00:03:35,000", text: "这位侍从和国王无比亲密的关系", original_text: "这位侍从和国王无比亲密的关系" },
        { id: 53, seek: 215, start: 215, end: 219, start_time: "00:03:35,000", end_time: "00:03:39,000", text: "使得这个职称变得极具影响力。", original_text: "使得这个职称变得极具影响力。" },
        { id: 54, seek: 219, start: 219, end: 224, start_time: "00:03:39,000", end_time: "00:03:44,000", text: "厕所科技发展史中的下一个大跃进 出现在 1596 年：", original_text: "厕所科技发展史中的下一个大跃进 出现在 1596 年：" },
        { id: 55, seek: 224, start: 224, end: 228, start_time: "00:03:44,000", end_time: "00:03:48,000", text: "约翰·哈林顿爵士（John Harringron） 为伊丽莎白女王设计了", original_text: "约翰·哈林顿爵士（John Harringron） 为伊丽莎白女王设计了" },
        { id: 56, seek: 228, start: 228, end: 230, start_time: "00:03:48,000", end_time: "00:03:50,000", text: "第一个现代抽水马桶。", original_text: "第一个现代抽水马桶。" },
        { id: 57, seek: 230, start: 230, end: 234, start_time: "00:03:50,000", end_time: "00:03:54,000", text: "它利用杠杆放水 并用阀门抽干桶中的水，", original_text: "它利用杠杆放水 并用阀门抽干桶中的水，" },
        { id: 58, seek: 234, start: 234, end: 237, start_time: "00:03:54,000", end_time: "00:03:57,000", text: "为现代马桶设计提供了参考。", original_text: "为现代马桶设计提供了参考。" },
        { id: 59, seek: 237, start: 237, end: 241, start_time: "00:03:57,000", end_time: "00:04:01,000", text: "但是哈林顿的发明 没有解决污水臭味的问题。", original_text: "但是哈林顿的发明 没有解决污水臭味的问题。" },
        { id: 60, seek: 241, start: 241, end: 246, start_time: "00:04:01,000", end_time: "00:04:06,000", text: "幸好到了 1775 年，苏格兰发明家 亚历山大·卡明（Alexander Cumming）", original_text: "幸好到了 1775 年，苏格兰发明家 亚历山大·卡明（Alexander Cumming）" },
        { id: 61, seek: 246, start: 246, end: 250, start_time: "00:04:06,000", end_time: "00:04:10,000", text: "在排水管道处加了一个弯曲的设计 进行保水限臭。", original_text: "在排水管道处加了一个弯曲的设计 进行保水限臭。" },
        { id: 62, seek: 250, start: 250, end: 257, start_time: "00:04:10,000", end_time: "00:04:17,000", text: "这个S型设计 经托马斯·克拉珀（Thomas Crapper） 改进成现代的U型设计——", original_text: "这个S型设计 经托马斯·克拉珀（Thomas Crapper） 改进成现代的U型设计——" },
        { id: 63, seek: 257, start: 257, end: 262, start_time: "00:04:17,000", end_time: "00:04:22,000", text: "尽管“废物”一词比发明者 出现早几个世纪。", original_text: "尽管“废物”一词比发明者 出现早几个世纪。" },
        { id: 64, seek: 262, start: 262, end: 264, start_time: "00:04:22,000", end_time: "00:04:24,000", text: "到了 19 世纪末，", original_text: "到了 19 世纪末，" },
        { id: 65, seek: 264, start: 264, end: 267, start_time: "00:04:24,000", end_time: "00:04:27,000", text: "许多城市都已经有了 现代污水基础设施", original_text: "许多城市都已经有了 现代污水基础设施" },
        { id: 66, seek: 267, start: 267, end: 269, start_time: "00:04:27,000", end_time: "00:04:29,000", text: "以及污水处理厂。", original_text: "以及污水处理厂。" },
        { id: 67, seek: 269, start: 269, end: 273, start_time: "00:04:29,000", end_time: "00:04:33,000", text: "如今，厕所也发展出了广泛的特征——", original_text: "如今，厕所也发展出了广泛的特征——" },
        { id: 68, seek: 273, start: 273, end: 276, start_time: "00:04:33,000", end_time: "00:04:36,000", text: "从奢华到可持续性。", original_text: "从奢华到可持续性。" },
        { id: 69, seek: 276, start: 276, end: 281, start_time: "00:04:36,000", end_time: "00:04:41,000", text: "然而仍有大约 20 亿的人 在家中没有于个人使用的厕所。", original_text: "然而仍有大约 20 亿的人 在家中没有于个人使用的厕所。" },
        { id: 70, seek: 281, start: 281, end: 284, start_time: "00:04:41,000", end_time: "00:04:44,000", text: "还有另外 22 亿人", original_text: "还有另外 22 亿人" },
        { id: 71, seek: 284, start: 284, end: 286, start_time: "00:04:44,000", end_time: "00:04:46,000", text: "没有适当管理废水的设施，", original_text: "没有适当管理废水的设施，" },
        { id: 72, seek: 286, start: 286, end: 290, start_time: "00:04:46,000", end_time: "00:04:50,000", text: "使他们被暴露在无数疾病的威胁下。", original_text: "使他们被暴露在无数疾病的威胁下。" },
        { id: 73, seek: 290, start: 290, end: 294, start_time: "00:04:50,000", end_time: "00:04:54,000", text: "为了解决这个问题， 我们需要发明新的卫生技术，", original_text: "为了解决这个问题， 我们需要发明新的卫生技术，" },
        { id: 74, seek: 294, start: 294, end: 299, start_time: "00:04:54,000", end_time: "00:04:59,000", text: "并解决会造成卫生管道系统不公平的", original_text: "并解决会造成卫生管道系统不公平的" },
        { id: 75, seek: 299, start: 299, end: 314, start_time: "00:04:59,000", end_time: "00:05:14,000", text: "行为、经济以及政治方面的诸多问题。", original_text: "行为、经济以及政治方面的诸多问题。" }
