        { id: 63, seek: 254, start: 254, end: 258, start_time: "00:04:14,000", end_time: "00:04:18,000", text: "限制捐赠超过标注期限的食物，", original_text: "限制捐赠超过标注期限的食物，" },
        { id: 64, seek: 258, start: 258, end: 262, start_time: "00:04:18,000", end_time: "00:04:22,000", text: "虽然联邦政府其实 是鼓励这类捐赠的。", original_text: "虽然联邦政府其实 是鼓励这类捐赠的。" },
        { id: 65, seek: 262, start: 262, end: 264, start_time: "00:04:22,000", end_time: "00:04:24,000", text: "像法国这样的国家更进一步，", original_text: "像法国这样的国家更进一步，" },
        { id: 66, seek: 264, start: 264, end: 269, start_time: "00:04:24,000", end_time: "00:04:29,000", text: "要求许多超市捐赠未售食品。", original_text: "要求许多超市捐赠未售食品。" },
        { id: 67, seek: 269, start: 269, end: 271, start_time: "00:04:29,000", end_time: "00:04:31,000", text: "无论你的政府怎么选，", original_text: "无论你的政府怎么选，" },
        { id: 68, seek: 271, start: 271, end: 275, start_time: "00:04:31,000", end_time: "00:04:35,000", text: "避免食物浪费的最佳方式 就是买什么吃什么。", original_text: "避免食物浪费的最佳方式 就是买什么吃什么。" },
        { id: 69, seek: 275, start: 275, end: 279, start_time: "00:04:35,000", end_time: "00:04:39,000", text: "别忘了，你的眼睛、鼻子和舌头", original_text: "别忘了，你的眼睛、鼻子和舌头" },
        { id: 70, seek: 279, start: 279, end: 282, start_time: "00:04:39,000", end_time: "00:04:42,000", text: "一般足够用来判断 这个食物该进你的肚子", original_text: "一般足够用来判断 这个食物该进你的肚子" },
        { id: 71, seek: 282, start: 282, end: 286, start_time: "00:04:42,000", end_time: "00:04:46,000", text: "还是厨余垃圾桶。", original_text: "还是厨余垃圾桶。" }
