        { id: 34, seek: 105, start: 105, end: 109, start_time: "00:01:45,000", end_time: "00:01:49,000", text: "这支宏伟的军队仍然精确地保持战备姿态", original_text: "这支宏伟的军队仍然精确地保持战备姿态" },
        { id: 35, seek: 109, start: 109, end: 112, start_time: "00:01:49,000", end_time: "00:01:52,000", text: "分布在几个坑中", original_text: "分布在几个坑中" },
        { id: 36, seek: 112, start: 112, end: 115, start_time: "00:01:52,000", end_time: "00:01:55,000", text: "一个包含了6000士兵的主力部队", original_text: "一个包含了6000士兵的主力部队" },
        { id: 37, seek: 115, start: 115, end: 117, start_time: "00:01:55,000", end_time: "00:01:57,000", text: "每个重几百磅", original_text: "每个重几百磅" },
        { id: 38, seek: 117, start: 117, end: 123, start_time: "00:01:57,000", end_time: "00:02:03,000", text: "另一个有130辆战车，及超过600匹战马", original_text: "另一个有130辆战车，及超过600匹战马" },
        { id: 39, seek: 123, start: 123, end: 125, start_time: "00:02:03,000", end_time: "00:02:05,000", text: "第三个坑存放高级统帅", original_text: "第三个坑存放高级统帅" },
        { id: 40, seek: 125, start: 125, end: 128, start_time: "00:02:05,000", end_time: "00:02:08,000", text: "空的第四号坑说明这个巨大的工程", original_text: "空的第四号坑说明这个巨大的工程" },
        { id: 41, seek: 128, start: 128, end: 131, start_time: "00:02:08,000", end_time: "00:02:11,000", text: "可能在皇帝驾崩时还没有完成", original_text: "可能在皇帝驾崩时还没有完成" },
        { id: 42, seek: 131, start: 131, end: 135, start_time: "00:02:11,000", end_time: "00:02:15,000", text: "此外，邻近的墓穴放了乐师、杂技演员、", original_text: "此外，邻近的墓穴放了乐师、杂技演员、" },
        { id: 43, seek: 135, start: 135, end: 137, start_time: "00:02:15,000", end_time: "00:02:17,000", text: "工人及官员的陶俑", original_text: "工人及官员的陶俑" },
        { id: 44, seek: 137, start: 137, end: 139, start_time: "00:02:17,000", end_time: "00:02:19,000", text: "及不同稀奇动物", original_text: "及不同稀奇动物" },
        { id: 45, seek: 139, start: 139, end: 142, start_time: "00:02:19,000", end_time: "00:02:22,000", text: "显示秦始皇对死后生活有更多的打算", original_text: "显示秦始皇对死后生活有更多的打算" },
        { id: 46, seek: 142, start: 142, end: 145, start_time: "00:02:22,000", end_time: "00:02:25,000", text: "而不限于战争", original_text: "而不限于战争" },
        { id: 47, seek: 145, start: 145, end: 149, start_time: "00:02:25,000", end_time: "00:02:29,000", text: "所有的人俑都由陶土及烤过的泥土造成", original_text: "所有的人俑都由陶土及烤过的泥土造成" },
        { id: 48, seek: 149, start: 149, end: 151, start_time: "00:02:29,000", end_time: "00:02:31,000", text: "那是一种泛红的棕色粘土", original_text: "那是一种泛红的棕色粘土" },
        { id: 49, seek: 151, start: 151, end: 157, start_time: "00:02:31,000", end_time: "00:02:37,000", text: "为了建造他们，许多工坊及据称超720,000个工匠", original_text: "为了建造他们，许多工坊及据称超720,000个工匠" },
        { id: 50, seek: 157, start: 157, end: 159, start_time: "00:02:37,000", end_time: "00:02:39,000", text: "被皇帝征召", original_text: "被皇帝征召" },
        { id: 51, seek: 159, start: 159, end: 163, start_time: "00:02:39,000", end_time: "00:02:43,000", text: "包括许多技工，他们将陶俑的分成许多模块", original_text: "包括许多技工，他们将陶俑的分成许多模块" },
        { id: 52, seek: 163, start: 163, end: 168, start_time: "00:02:43,000", end_time: "00:02:48,000", text: "逐一雕塑皇帝的每个战士", original_text: "逐一雕塑皇帝的每个战士" },
        { id: 53, seek: 168, start: 168, end: 170, start_time: "00:02:48,000", end_time: "00:02:50,000", text: "他们依序排列", original_text: "他们依序排列" },
        { id: 54, seek: 170, start: 170, end: 172, start_time: "00:02:50,000", end_time: "00:02:52,000", text: "都有不同的武器和制服", original_text: "都有不同的武器和制服" },
        { id: 55, seek: 172, start: 172, end: 174, start_time: "00:02:52,000", end_time: "00:02:54,000", text: "不同的发型及表情", original_text: "不同的发型及表情" },
        { id: 56, seek: 174, start: 174, end: 176, start_time: "00:02:54,000", end_time: "00:02:56,000", text: "甚至不同的耳朵", original_text: "甚至不同的耳朵" },
        { id: 57, seek: 176, start: 176, end: 179, start_time: "00:02:56,000", end_time: "00:02:59,000", text: "最初，每个战士都被涂明亮的颜色", original_text: "最初，每个战士都被涂明亮的颜色" },
        { id: 58, seek: 179, start: 179, end: 183, start_time: "00:02:59,000", end_time: "00:03:03,000", text: "但暴露在空气中使得涂料褪色", original_text: "但暴露在空气中使得涂料褪色" },
        { id: 59, seek: 183, start: 183, end: 185, start_time: "00:03:03,000", end_time: "00:03:05,000", text: "只剩下陶俑本身", original_text: "只剩下陶俑本身" },
        { id: 60, seek: 185, start: 185, end: 189, start_time: "00:03:05,000", end_time: "00:03:09,000", text: "也是因为这个原因，在一英里之内的另一个墓穴", original_text: "也是因为这个原因，在一英里之内的另一个墓穴" },
        { id: 61, seek: 189, start: 189, end: 191, start_time: "00:03:09,000", end_time: "00:03:11,000", text: "尚未被挖掘", original_text: "尚未被挖掘" },
        { id: 62, seek: 191, start: 191, end: 194, start_time: "00:03:11,000", end_time: "00:03:14,000", text: "这是真正的秦始皇陵", original_text: "这是真正的秦始皇陵" },
        { id: 63, seek: 194, start: 194, end: 198, start_time: "00:03:14,000", end_time: "00:03:18,000", text: "据称含有宫殿，珍贵的石头及工艺品", original_text: "据称含有宫殿，珍贵的石头及工艺品" },
        { id: 64, seek: 198, start: 198, end: 202, start_time: "00:03:18,000", end_time: "00:03:22,000", text: "甚至有水银做成的河", original_text: "甚至有水银做成的河" },
        { id: 65, seek: 202, start: 202, end: 206, start_time: "00:03:22,000", end_time: "00:03:26,000", text: "但除非能发现一种可以打开陵墓并不损坏宝藏的方法", original_text: "但除非能发现一种可以打开陵墓并不损坏宝藏的方法" },
        { id: 66, seek: 206, start: 206, end: 210, start_time: "00:03:26,000", end_time: "00:03:30,000", text: "陵墓仍然被密封", original_text: "陵墓仍然被密封" },
        { id: 67, seek: 210, start: 210, end: 213, start_time: "00:03:30,000", end_time: "00:03:33,000", text: "秦始皇不是唯一的希望人们陪他走到最后的人", original_text: "秦始皇不是唯一的希望人们陪他走到最后的人" },
        { id: 68, seek: 213, start: 213, end: 218, start_time: "00:03:33,000", end_time: "00:03:38,000", text: "古埃及陵墓包含着象征着理想死后生活的泥俑", original_text: "古埃及陵墓包含着象征着理想死后生活的泥俑" },
        { id: 69, seek: 218, start: 218, end: 223, start_time: "00:03:38,000", end_time: "00:03:43,000", text: "日本古坟时代人死后", original_text: "日本古坟时代人死后" },
        { id: 70, seek: 223, start: 223, end: 226, start_time: "00:03:43,000", end_time: "00:03:46,000", text: "与马和房屋的雕塑一起埋葬", original_text: "与马和房屋的雕塑一起埋葬" },
        { id: 71, seek: 226, start: 226, end: 233, start_time: "00:03:46,000", end_time: "00:03:53,000", text: "墨西哥海岸附件的耆那岛的洞穴中", original_text: "墨西哥海岸附件的耆那岛的洞穴中" },
        { id: 72, seek: 233, start: 233, end: 236, start_time: "00:03:53,000", end_time: "00:03:56,000", text: "充满了陶瓷俑", original_text: "充满了陶瓷俑" },
        { id: 73, seek: 236, start: 236, end: 241, start_time: "00:03:56,000", end_time: "00:04:01,000", text: "幸运的是，虽然秦始皇十分冷酷无情", original_text: "幸运的是，虽然秦始皇十分冷酷无情" },
        { id: 74, seek: 241, start: 241, end: 245, start_time: "00:04:01,000", end_time: "00:04:05,000", text: "他却选择建造仆人和士兵人俑陪葬", original_text: "他却选择建造仆人和士兵人俑陪葬" },
        { id: 75, seek: 245, start: 245, end: 248, start_time: "00:04:05,000", end_time: "00:04:08,000", text: "而不是牺牲真正的人来殉葬", original_text: "而不是牺牲真正的人来殉葬" },
        { id: 76, seek: 248, start: 248, end: 252, start_time: "00:04:08,000", end_time: "00:04:12,000", text: "在埃及、西非、安纳托利亚及北美部分地区", original_text: "在埃及、西非、安纳托利亚及北美部分地区" },
        { id: 77, seek: 252, start: 252, end: 254, start_time: "00:04:12,000", end_time: "00:04:14,000", text: "真人殉葬都被使用", original_text: "真人殉葬都被使用" },
        { id: 78, seek: 254, start: 254, end: 257, start_time: "00:04:14,000", end_time: "00:04:17,000", text: "甚至存在于中国的商周朝时期", original_text: "甚至存在于中国的商周朝时期" },
        { id: 79, seek: 257, start: 257, end: 262, start_time: "00:04:17,000", end_time: "00:04:22,000", text: "今天， 人们从世界各地赶来参观这些守望的士兵", original_text: "今天， 人们从世界各地赶来参观这些守望的士兵" },
        { id: 80, seek: 262, start: 262, end: 252, start_time: "00:04:22,000", end_time: "00:04:12,000", text: "他们已经在这等待出征的号角已经几个世纪了", original_text: "他们已经在这等待出征的号角已经几个世纪了" }
