        { id: 15, seek: 63, start: 63, end: 67, start_time: "00:01:03,000", end_time: "00:01:07,000", text: "宛如迷你水气球，从种子表面向外膨胀。", original_text: "宛如迷你水气球，从种子表面向外膨胀。" },
        { id: 16, seek: 67, start: 67, end: 113, start_time: "00:01:07,000", end_time: "00:01:13,000", text: "不论长成多大个，每根棉纤维", original_text: "不论长成多大个，每根棉纤维" },
        { id: 17, seek: 113, start: 113, end: 116, start_time: "00:01:13,000", end_time: "00:01:16,000", text: "这种细胞有好几层细胞壁。", original_text: "这种细胞有好几层细胞壁。" },
        { id: 18, seek: 116, start: 116, end: 119, start_time: "00:01:16,000", end_time: "00:01:19,000", text: "几天后，第一层细胞壁，", original_text: "几天后，第一层细胞壁，" },
        { id: 19, seek: 119, start: 119, end: 122, start_time: "00:01:19,000", end_time: "00:01:22,000", text: "亦称为初生细胞壁，", original_text: "亦称为初生细胞壁，" },
        { id: 20, seek: 122, start: 122, end: 125, start_time: "00:01:22,000", end_time: "00:01:25,000", text: "变硬，向一个方向推动细胞生长，", original_text: "变硬，向一个方向推动细胞生长，" },
        { id: 21, seek: 125, start: 125, end: 128, start_time: "00:01:25,000", end_time: "00:01:28,000", text: "从而使纤维被拉长。", original_text: "从而使纤维被拉长。" },
        { id: 22, seek: 128, start: 128, end: 132, start_time: "00:01:28,000", end_time: "00:01:32,000", text: "在16天左右的时间，纤维被快速拉长。", original_text: "在16天左右的时间，纤维被快速拉长。" },
        { id: 23, seek: 132, start: 132, end: 136, start_time: "00:01:32,000", end_time: "00:01:36,000", text: "其后，它便进入下一个生长阶段：强化细胞壁。", original_text: "其后，它便进入下一个生长阶段：强化细胞壁。" },
        { id: 24, seek: 136, start: 136, end: 140, start_time: "00:01:36,000", end_time: "00:01:40,000", text: "这是通过产生更多的碳水化合物纤维素来完成的。", original_text: "这是通过产生更多的碳水化合物纤维素来完成的。" },
        { id: 25, seek: 140, start: 140, end: 144, start_time: "00:01:40,000", end_time: "00:01:44,000", text: "在这一阶段，该纤维素占细胞壁构成成分的34%，", original_text: "在这一阶段，该纤维素占细胞壁构成成分的34%，" },
        { id: 26, seek: 144, start: 144, end: 146, start_time: "00:01:44,000", end_time: "00:01:46,000", text: "且占比仍旧快速上升。", original_text: "且占比仍旧快速上升。" },
        { id: 27, seek: 146, start: 146, end: 150, start_time: "00:01:46,000", end_time: "00:01:50,000", text: "这一生长促使对现有细胞壁纹理的抵触，", original_text: "这一生长促使对现有细胞壁纹理的抵触，" },
        { id: 28, seek: 150, start: 150, end: 153, start_time: "00:01:50,000", end_time: "00:01:53,000", text: "因此也强化了细胞壁。", original_text: "因此也强化了细胞壁。" },
        { id: 29, seek: 153, start: 153, end: 157, start_time: "00:01:53,000", end_time: "00:01:57,000", text: "被强化的细胞壁变得更加坚硬，会限制其进一步生长。", original_text: "被强化的细胞壁变得更加坚硬，会限制其进一步生长。" },
        { id: 30, seek: 157, start: 157, end: 160, start_time: "00:01:57,000", end_time: "00:02:00,000", text: "这意味着如果纤维素过早改变细胞壁结构，", original_text: "这意味着如果纤维素过早改变细胞壁结构，" },
        { id: 31, seek: 160, start: 160, end: 162, start_time: "00:02:00,000", end_time: "00:02:02,000", text: "棉花纤维就会很短，", original_text: "棉花纤维就会很短，" },
        { id: 32, seek: 162, start: 162, end: 165, start_time: "00:02:02,000", end_time: "00:02:05,000", text: "最终只能产出粗糙且易破的布料。", original_text: "最终只能产出粗糙且易破的布料。" },
        { id: 33, seek: 165, start: 165, end: 168, start_time: "00:02:05,000", end_time: "00:02:08,000", text: "但如果细胞壁强化阶段开始得过晚，", original_text: "但如果细胞壁强化阶段开始得过晚，" },
        { id: 34, seek: 168, start: 168, end: 170, start_time: "00:02:08,000", end_time: "00:02:10,000", text: "那层细胞壁就会不够坚固，", original_text: "那层细胞壁就会不够坚固，" },
        { id: 35, seek: 170, start: 170, end: 174, start_time: "00:02:10,000", end_time: "00:02:14,000", text: "生成的纤维会过为柔弱，难以很好地将布料合成。", original_text: "生成的纤维会过为柔弱，难以很好地将布料合成。" },
        { id: 36, seek: 174, start: 174, end: 176, start_time: "00:02:14,000", end_time: "00:02:16,000", text: "理想的生长条件是：", original_text: "理想的生长条件是：" },
        { id: 37, seek: 176, start: 176, end: 183, start_time: "00:02:16,000", end_time: "00:02:23,000", text: "适宜的温度、水、肥料、害虫防治，还有光线。", original_text: "适宜的温度、水、肥料、害虫防治，还有光线。" },
        { id: 38, seek: 183, start: 183, end: 188, start_time: "00:02:23,000", end_time: "00:02:28,000", text: "这样，一根棉纤维可以生长至长3.6厘米，", original_text: "这样，一根棉纤维可以生长至长3.6厘米，" },
        { id: 39, seek: 188, start: 188, end: 191, start_time: "00:02:28,000", end_time: "00:02:31,000", text: "宽仅为25微米。", original_text: "宽仅为25微米。" },
        { id: 40, seek: 191, start: 191, end: 195, start_time: "00:02:31,000", end_time: "00:02:35,000", text: "细长且高品质的纤维相较于短且质量较次的纤维，", original_text: "细长且高品质的纤维相较于短且质量较次的纤维，" },
        { id: 41, seek: 195, start: 195, end: 197, start_time: "00:02:35,000", end_time: "00:02:37,000", text: "能更好地缠绕彼此。", original_text: "能更好地缠绕彼此。" },
        { id: 42, seek: 197, start: 197, end: 201, start_time: "00:02:37,000", end_time: "00:02:41,000", text: "这表示那些细长且高品质的纤维能够制造出更坚韧的棉线，", original_text: "这表示那些细长且高品质的纤维能够制造出更坚韧的棉线，" },
        { id: 43, seek: 201, start: 201, end: 204, start_time: "00:02:41,000", end_time: "00:02:44,000", text: "编织成更好的面料。", original_text: "编织成更好的面料。" },
        { id: 44, seek: 204, start: 204, end: 207, start_time: "00:02:44,000", end_time: "00:02:47,000", text: "因为棉花的特性，它们被广泛使用，", original_text: "因为棉花的特性，它们被广泛使用，" },
        { id: 45, seek: 207, start: 207, end: 209, start_time: "00:02:47,000", end_time: "00:02:49,000", text: "从柔软的纺织品到美元纸钞——", original_text: "从柔软的纺织品到美元纸钞——" },
        { id: 46, seek: 209, start: 209, end: 212, start_time: "00:02:49,000", end_time: "00:02:52,000", text: "内含75%的棉成分。", original_text: "内含75%的棉成分。" },
        { id: 47, seek: 212, start: 212, end: 215, start_time: "00:02:52,000", end_time: "00:02:55,000", text: "在下一个棉花纤维成长的关键阶段，", original_text: "在下一个棉花纤维成长的关键阶段，" },
        { id: 48, seek: 215, start: 215, end: 219, start_time: "00:02:55,000", end_time: "00:02:59,000", text: "大量纤维素在次生细胞壁内", original_text: "大量纤维素在次生细胞壁内" },
        { id: 49, seek: 219, start: 219, end: 223, start_time: "00:02:59,000", end_time: "00:03:03,000", text: "堆积并从而强化细胞壁。", original_text: "堆积并从而强化细胞壁。" },
        { id: 50, seek: 223, start: 223, end: 228, start_time: "00:03:03,000", end_time: "00:03:08,000", text: "纤维素的重量占比上升，超过纤维净重的90%。", original_text: "纤维素的重量占比上升，超过纤维净重的90%。" },
        { id: 51, seek: 228, start: 228, end: 230, start_time: "00:03:08,000", end_time: "00:03:10,000", text: "越多纤维素堆积，", original_text: "越多纤维素堆积，" },
        { id: 52, seek: 230, start: 230, end: 233, start_time: "00:03:10,000", end_time: "00:03:13,000", text: "次生细胞壁就变得更紧实——", original_text: "次生细胞壁就变得更紧实——" },
        { id: 53, seek: 233, start: 233, end: 236, start_time: "00:03:13,000", end_time: "00:03:16,000", text: "这决定了最终棉花纤维的强度。", original_text: "这决定了最终棉花纤维的强度。" },
        { id: 54, seek: 236, start: 236, end: 239, start_time: "00:03:16,000", end_time: "00:03:19,000", text: "对于后阶段是否能产出经久耐用的材料，比如T恤，", original_text: "对于后阶段是否能产出经久耐用的材料，比如T恤，" },
        { id: 55, seek: 239, start: 239, end: 242, start_time: "00:03:19,000", end_time: "00:03:22,000", text: "这一生长阶段至关重要。", original_text: "这一生长阶段至关重要。" },
        { id: 56, seek: 242, start: 242, end: 246, start_time: "00:03:22,000", end_time: "00:03:26,000", text: "一件成衣经受多年穿洗的能力", original_text: "一件成衣经受多年穿洗的能力" },
        { id: 57, seek: 246, start: 246, end: 250, start_time: "00:03:26,000", end_time: "00:03:30,000", text: "很大程度上取决于次生细胞壁的密度。", original_text: "很大程度上取决于次生细胞壁的密度。" },
        { id: 58, seek: 250, start: 250, end: 252, start_time: "00:03:30,000", end_time: "00:03:32,000", text: "从另一方面来说，", original_text: "从另一方面来说，" },
        { id: 59, seek: 252, start: 252, end: 255, start_time: "00:03:32,000", end_time: "00:03:35,000", text: "成衣的柔软性主要受到棉纤维长度的影响，", original_text: "成衣的柔软性主要受到棉纤维长度的影响，" },
        { id: 60, seek: 255, start: 255, end: 260, start_time: "00:03:35,000", end_time: "00:03:40,000", text: "而纤维长度是由初生细胞壁重塑的。", original_text: "而纤维长度是由初生细胞壁重塑的。" },
        { id: 61, seek: 260, start: 260, end: 264, start_time: "00:03:40,000", end_time: "00:03:44,000", text: "终于，在大约50天后，纤维生长完毕。", original_text: "终于，在大约50天后，纤维生长完毕。" },
        { id: 62, seek: 264, start: 264, end: 267, start_time: "00:03:44,000", end_time: "00:03:47,000", text: "细胞死亡，", original_text: "细胞死亡，" },
        { id: 63, seek: 267, start: 267, end: 270, start_time: "00:03:47,000", end_time: "00:03:50,000", text: "只留下纤维素。", original_text: "只留下纤维素。" },
        { id: 64, seek: 270, start: 270, end: 275, start_time: "00:03:50,000", end_time: "00:03:55,000", text: "包裹纤维的干燥的棉籽荚，也叫棉铃裂开了，", original_text: "包裹纤维的干燥的棉籽荚，也叫棉铃裂开了，" },
        { id: 65, seek: 275, start: 275, end: 281, start_time: "00:03:55,000", end_time: "00:04:01,000", text: "露出一簇簇柔软蓬松的纤维细胞。", original_text: "露出一簇簇柔软蓬松的纤维细胞。" },
        { id: 66, seek: 281, start: 281, end: 285, start_time: "00:04:01,000", end_time: "00:04:05,000", text: "我们看到的丝状纤维——比人类毛发还要细——", original_text: "我们看到的丝状纤维——比人类毛发还要细——" },
        { id: 67, seek: 285, start: 285, end: 289, start_time: "00:04:05,000", end_time: "00:04:09,000", text: "是那些紧实干燥的纤维素壁（原细胞壁）的残留物。", original_text: "是那些紧实干燥的纤维素壁（原细胞壁）的残留物。" },
        { id: 68, seek: 289, start: 289, end: 292, start_time: "00:04:09,000", end_time: "00:04:12,000", text: "数以万计的这些纤维被纺织成纱线，", original_text: "数以万计的这些纤维被纺织成纱线，" },
        { id: 69, seek: 292, start: 292, end: 297, start_time: "00:04:12,000", end_time: "00:04:17,000", text: "随后继续加工生产为各种东西，从面料织物，到咖啡过滤器、尿布，", original_text: "随后继续加工生产为各种东西，从面料织物，到咖啡过滤器、尿布，" },
        { id: 70, seek: 297, start: 297, end: 299, start_time: "00:04:17,000", end_time: "00:04:19,000", text: "还有渔网。", original_text: "还有渔网。" },
        { id: 71, seek: 299, start: 299, end: 301, start_time: "00:04:19,000", end_time: "00:04:21,000", text: "利用现代科学的帮助，", original_text: "利用现代科学的帮助，" },
        { id: 72, seek: 301, start: 301, end: 305, start_time: "00:04:21,000", end_time: "00:04:25,000", text: "调整营养、气候和基因等学科因素，", original_text: "调整营养、气候和基因等学科因素，" },
        { id: 73, seek: 305, start: 305, end: 309, start_time: "00:04:25,000", end_time: "00:04:29,000", text: "科研人员正在研究如何优化棉花生长。", original_text: "科研人员正在研究如何优化棉花生长。" },
        { id: 74, seek: 309, start: 309, end: 312, start_time: "00:04:29,000", end_time: "00:04:32,000", text: "棉花可能在不久将来变得更柔软、更坚硬，以及更有韧性。", original_text: "棉花可能在不久将来变得更柔软、更坚硬，以及更有韧性。" }
